catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('x_1428369_lva.getUserlocationInfo'); //GetUserLocationInfo is the Script Include class
    ga.addParam('sysparm_name', 'GetLocation'); // GetLocation is the method in GetUserLocationInfo Script Include class
    ga.addParam('sysparm_user_id', g_form.getValue("requested_for")); // Get the value of requested_for field and place it the sysparam_user_name
	//alert('Requested for value is - ' + g_form.getValue('requested_for'));
    ga.getXML(SetLocation); //SetLocation is the callback function store the location of the user   

}

/* Callback function for getting the location from the server side code*/
function SetLocation(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");   
    g_form.setValue('office_location', answer);

}
