sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var vehiStatus = current.vehicle_status;
	var vehiCatalogSysId =  current.vehicle.sys_id;
	gs.info("Vehicle status is - " + vehiStatus);

	var lva_GR = new GlideRecord('x_1428369_lva_loaner_vehicle_catalog');
	lva_GR.addQuery('sys_id', vehiCatalogSysId);
	lva_GR.query();

	if(lva_GR.next()){
		gs.info("Entered the if block!");
		if(vehiStatus == "Pending Release" || vehiStatus == "Out on Field" || vehiStatus == "Returned for Inspection" || vehiStatus == "Sent for Servicing") 
		{
			lva_GR.status = "Unavailable";
			gs.info(" stage 1 Loaner catalog status - " + lva_GR.status);
		}
		else if(vehiStatus == "Returned to Warehouse") { 
			lva_GR.status = "Available";
			gs.info("Stage 2 Loaner catalog status - " + lva_GR.status); }
		else if(vehiStatus == "Decommissioned") {
			lva_GR.status = "Decommissioned";
			gs.info("Stage 3 Loaner catalog status - " + lva_GR.status);
		}
		lva_GR.update();
	}

})(current, previous);
