sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var vehiTrackerNumber = current.sys_id;
	var vehiCatalogNumber = current.vehicle;	
	gs.info("Vehicle trackernumber is - " + vehiTrackerNumber);
	gs.info("Vehicle catalog number is - " + vehiCatalogNumber);	

	var lva_vs_GR = new GlideRecord('x_1428369_lva_vehicle_servicing');
	lva_vs_GR.addQuery('vehicle_tracker', vehiTrackerNumber);
	lva_vs_GR.addQuery('ticket_status', "Open");
	lva_vs_GR.query();

	if(!lva_vs_GR.next())
	{
		gs.info("Entered the if block - Create a new ticket");

		var new_lva_vs_GR = new GlideRecord('x_1428369_lva_vehicle_servicing');
		var date_vs = new GlideDate().getValue();
		gs.info('Glide date is - ' + date_vs);

		new_lva_vs_GR.initialize();
		new_lva_vs_GR.vehicle_tracker = vehiTrackerNumber;
		new_lva_vs_GR.vehicle = vehiCatalogNumber;
		new_lva_vs_GR.vehicle_status = "In Service";
		new_lva_vs_GR.date_sent_for_servicing = date_vs;
		new_lva_vs_GR.ticket_status = "Open";
		gs.info("New record vehicle tracker - " + new_lva_vs_GR.vehicle_tracker);

		new_lva_vs_GR.insert();
	}

})(current, previous);
