sys_script_include.script:
var getUserlocationInfo = Class.create();
getUserlocationInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    GetLocation: function() {

       gs.info("Am in script include");
        var locatn = '';
        var userId = this.getParameter('sysparm_user_id');
        gs.info('user id - ' + userId);

        var gr = new GlideRecord('sys_user');
        gr.addQuery('sys_id', userId);
        gr.query();

        if (gr.next()) {
            locatn = gr.getValue('location'); // location is the name taken from Location field of User table form.
            gs.info('In GetLocation Function - ' + locatn); // Debugging           
        }
        return locatn;

    },
    type: 'getUserlocationInfo'
});
